<?php/** * Represents a smf midi Track in a object structure. * @author Ddimo Vieira de Arajo Junior * @property-read array event An array of Events. * @method addEvent() addTrack(Event $event) Adds a Event in the MidiFile Track. * @method removeEvent() removeEvent(int $index) Removes the Event specified by the $index parameter. * @method string toString() toString() Gives a string representation for the Track object. */class Track extends Entity {	/**	 * Builds a new Track object structure.	 */	public function __construct(){		$this->properties = array(			"event" => array()		);	}		/**	 * Validates a new value for a property. It's a implementation for the abstract method from Entity class.	 * @param string $propertyName Property name to be validated.	 * @param string $newValue The new value for vilidating.	 * @return bool Returns true if the new value is ok for the property.	 */	protected function validate($propertyName, $newValue){		throw new ReadOnlyPropertyException("Track->event is a read only property.");	}		/**	 * Adds a Event in the Track.	 * @param Event $event The Event object to be added.	 */	public function addEvent(Event $event){		$this->properties["event"][] = $event;	}		/**	 * Removes the Event specified by the $index parameter.	 * @param int $index The index of the Event to be removed.	 */	public function removeEvent($index){		array_splice($this->properties["event"], $index, 1);	}		/**	 * Gives a string representation for the Track object.	 */	public function toString(){		return $this->__toString();	}	public function __toString(){		$buf = "".count($this->event)." Event(s)";		foreach ($this->properties["event"] as $event){			$buf .= "\n".$event->toString();		}		return $buf;	}}?>